css:
    body {
        font-family: Arial, sans-serif;
        font-size: 12px;
    }

    .dart-report {
        padding: 20px;
    }

    .dart-header {
        text-align: center;
        font-weight: bold;
        font-size: 18px;
        margin-bottom: 20px;
    }

    .dart-table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 10px;
    }

    .dart-table td {
        padding: 4px;
        vertical-align: top;
        font-size: 12px;
    }

    .section-title {
        font-weight: bold;
        font-size: 14px;
        margin: 15px 0 4px;
        border-top: 1px solid #000;
        padding-top: 10px;
    }

    .bold-label {
        font-weight: bold;
    }

    .image-container {
        display: flex;
        justify-content: start;
        gap: 30px;
         margin: 14px 0px -10px 30px;

    }
    


    .image-container img {
        max-height: 250px;
        border: 1px solid #ccc;
        padding: 4px;
    }

    .graph-caption {
        font-size: 10px;
        margin-top: 4px;
    }

    .signature {
        margin-top: 0px;
        text-align: right;
    }

    .signature img {
        max-height: 100px;
    }

    .disclaimer {
        font-size: 9px;
        margin-top: 0px;
        font-style: italic;
        text-align: center;
        border-top: 1px solid #ccc;
        padding-top: 8px;
    }
    .table-bordered th tr{
    border: 1px solid #171717 !important;
}
